export const code = async (inputs: any) => {
  if (!inputs || typeof inputs.link !== "string") {
    return { success: false, error: "Input 'link' is required" };
  }

  // Download with browser-like headers
  const response = await fetch(inputs.link, {
    headers: {
      "Accept": "image/*",
      "User-Agent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64)"
    }
  });

  if (!response || response.status !== 200) {
    return {
      success: false,
      status: response ? response.status : "unknown",
      error: "Failed to download image"
    };
  }

  const contentType = response.headers.get("content-type") || "";

  // Reject HTML
  if (contentType.includes("text/html")) {
    return {
      success: false,
      error: "Received HTML instead of image",
      contentType
    };
  }

  // Read binary
  const buffer = Buffer.from(await response.arrayBuffer());
  const base64 = buffer.toString("base64");

  return {
    success: true,
    mime: contentType,   // keep for reference
    size_bytes: buffer.length,
    base64               // RAW base64 only
  };
};

import sharp from "sharp";

export const code = async (inputs) => {
  const { imageBase64, format } = inputs;

  try {
    if (!imageBase64) throw new Error("No imageBase64 provided");
    if (!["png", "jpg", "jpeg"].includes(format)) throw new Error("Unsupported format");

    // Clean base64 (remove data URI if exists)
    let cleanBase64 = imageBase64;
    if (imageBase64.startsWith("data:")) {
      cleanBase64 = imageBase64.split(",")[1];
    }

    const imageBuffer = Buffer.from(cleanBase64, "base64");

    let image = sharp(imageBuffer);

    if (format === "png") image = image.png();
    else image = image.jpeg({ quality: 90 });

    const outputBuffer = await image.toBuffer();
    const outputBase64 = outputBuffer.toString("base64");

    return { success: true, outputBase64, message: `Image converted to ${format}` };
  } catch (error) {
    return { success: false, message: error.message };
  }
};

export const code = async ({ base64 }) => {
  // Ensure correct Base64 format
  const cleanBase64 = base64.startsWith("data:image")
    ? base64.split(",")[1]
    : base64;

  // Convert Base64 to Blob
  const byteCharacters = atob(cleanBase64);
  const byteNumbers = new Array(byteCharacters.length);

  for (let i = 0; i < byteCharacters.length; i++) {
    byteNumbers[i] = byteCharacters.charCodeAt(i);
  }

  const byteArray = new Uint8Array(byteNumbers);
  const blob = new Blob([byteArray], { type: "image/png" });

  // Prepare form-data
  const formData = new FormData();
  formData.append("file", blob, "image.png");

  // Upload to image2url
  const response = await fetch("https://www.image2url.com/api/upload", {
    method: "POST",
    body: formData,
  });

  const result = await response.json();

  if (!result.success) {
    throw new Error("Image2URL upload failed");
  }

  return {
    image_url: result.url,
    filename: result.filename,
    size: result.size,
  };
};
